insert into Occupation_Designer(ModuleName,Content,DataFrom,[Order],IsPage,DesignerDescription)
values('职业健康检查指引单','LODOP.PRINT_INIT("");  LODOP.ADD_PRINT_HTM(100,65,800,1000,"<!DOCTYPE html>\r\n<html>\r\n<head>\r\n    <meta charset=\"utf-8\" />\r\n    <title>职业健康检查指引单</title>\r\n    <style type=\"text/css\">\r\n        *{padding: 0; margin: 0;}\r\n        .a4 {width: 660px;height: 842px; padding: 0.5rem 0.5rem;}\r\n        .left{text-align: left;}\r\n        .short{width: 60%;}\r\n        .short1{width: 44%;}\r\n        .tab1 tr td h4{display: inline-block; text-indent: 10px; font-size: 12px; font-weight: 400; border-bottom: 1px solid #888;}\r\n        .tab2 thead tr{height: 2rem;}\r\n        .tab2 tbody tr{height: 1.7rem;}\r\n        .tab2 tfoot tr{height: 2rem;}\r\n        .L{display: inline-block; text-align: left; width: 60px; }\r\n        .bb{border-right: 1px solid #888888; text-align: left; width: 26.7%; text-indent: 10px;}\r\n        .em{text-indent: 55px; display: inline-block;}\r\n        .td_indent{text-align: left; text-indent: 20px; font-weight: 600;}\r\n        .td_indent input{display: inline-block; margin-right: 10px;}\r\n        h5{margin-bottom: 0.25rem;font-family: \"微软雅黑\";font-size: 12px; background-color: darkgray; width: 25%;}\r\n        p{margin-bottom: 0.25rem;font-family: \"微软雅黑\";font-size: 12px; height: 20px; margin-top: 10px;}\r\n        table{width: 100%; -height: 100%;border-collapse: collapse;text-align: center;}\r\n        table tr{height: 2.5rem;line-height: 100%;}\r\n        table tr td{font-family: \"微软雅黑\";font-size: 14px;line-height: 1.5rem;}\r\n        caption{margin-bottom: 20px;}\r\n        caption h1{font-weight: 500; font-size: 28px;} \r\n    </style>\r\n</head>  \r\n<body><script>window.alert = null;window.confirm = null;window.open = null;window.showModalDialog = null;</script>\r\n        <div class='DivIndex0'>\r\n          <div class=\"a4\">\r\n            <table class=\"tab1\">\r\n                <caption><h1>四川省疾病预防控制中心<br/>职业健康检查指引单</h1></caption>\r\n                <tr>\r\n                    <td style=\"text-align: left;\"><span>体检编号:<span></span></span></td>\r\n                    <td colspan=\"3\" style=\"text-align: start;\"></td>\r\n                    <td rowspan=\"3\" style=\"width: 110px;\"><img src=\"@Picture\" width=\"100\" height=\"140\"/></td>\r\n                </tr>\r\n                <tr>\r\n                    <td class=\"left\">姓名:<h4 class=\"short\">@MName</h4></td>\r\n                    <td class=\"left\">性别:<h4 class=\"short1\">@MSex</h4></td>\r\n                    <td class=\"left\">年龄:<h4 class=\"short\">@MAge</h4></td>\r\n                    <td class=\"left\">民族:<h4 class=\"short\">@Nation</h4></td>\r\n                </tr>\r\n                <tr>\r\n                    <td class=\"left\">文化程度:<h4 class=\"short1\">@Degree</h4></td>\r\n                    <td class=\"left\">婚否:<h4 class=\"short1\">@IsMarry</h4></td>\r\n                    <td class=\"left\">工种:<h4 class=\"short\">@NowJob</h4></td>\r\n                    <td class=\"left\">工龄:<h4 class=\"short\">@AllWorkYear</h4></td>\r\n                </tr>\r\n                <tr>\r\n                    <td colspan=\"2\" class=\"left\">危害因素或特殊作业:<h4 class=\"short1\">@DangerousEffects</h4></td>\r\n                    <td colspan=\"3\" class=\"left\">职业危害工龄:<h4 class=\"short1\">@DangerousYear</h4></td>\r\n                </tr>\r\n                <tr>\r\n                    <td class=\"left\">检查类型:<h4 class=\"short\">@MemberType</h4></td>\r\n                    <td colspan=\"2\" class=\"left\">检查种类:<h4 class=\"short\">@PhysicalType</h4></td>\r\n                    <td colspan=\"2\" class=\"left\">检查表名称:<h4 class=\"short\">@TemplateName</h4></td>\r\n                </tr>\r\n                <tr>\r\n                    <td colspan=\"5\" class=\"left\">单位名称:<h4 class=\"short\" style=\"width: 87.5%;\">@UnitName</h4></td>\r\n                </tr>\r\n            </table>\r\n            <h5>注：打勾的为需体检的项目。</h5>\r\n            <table  class=\"tab2\" border=\"1\" bordercolor=\"#888888\">\r\n                <thead>\r\n                        <tr>\r\n                                <th colspan=\"2\">体检科室名称</th>\r\n                                <th>医师签字</th>\r\n                                <th colspan=\"2\">体检科室名称</th>\r\n                                <th>医师签字</th>\r\n                        </tr>\r\n                </thead>\r\n                <tbody>\r\n                    <tr>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />五官科</td>\r\n                        <td></td>\r\n                        <td colspan=\"2\"></td>\r\n                        <td></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />内科</td>\r\n                        <td></td>\r\n                        <td colspan=\"2\"></td>\r\n                        <td></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />外科</td>\r\n                        <td></td>\r\n                        <td colspan=\"2\"></td>\r\n                        <td></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />心电科</td>\r\n                        <td></td>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />血常规化验科</td>\r\n                        <td></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />X光影像科</td>\r\n                        <td></td>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />尿常规化验科</td>\r\n                        <td></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />B超影像科</td>\r\n                        <td></td>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />染色体化验科</td>\r\n                        <td></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />受检者个人信息录入</td>\r\n                        <td></td>\r\n                        <td colspan=\"2\" class=\"td_indent\"><input type=\"checkbox\" checked=\"checked\" />生化科</td>\r\n                        <td></td>\r\n                    </tr>\r\n                </tbody>\r\n                <tfoot>\r\n                    <tr>\r\n                        <td colspan=\"6\">\r\n                            <table bordercolor=\"#888888\" class=\"tab3\">\r\n                                <tr>\r\n                                    <td class=\"bb\">收缩压:<span class=\"em\">mmHg</span></td>\r\n                                    <td class=\"bb\">舒张压:<span class=\"em\">mmHg</span></td>\r\n                                    <td class=\"bb\">心率:<span class=\"em\">次/分</span></td>\r\n                                    <td width=\"14.5%\"></td>\r\n                                </tr>\r\n                            </table>\r\n                        </td>\r\n                    </tr>\r\n                </tfoot>\r\n            </table>\r\n            <p>备注：1、进行受检者个人信息录入前请先测血压、心率。</p>\r\n            <p style=\"text-indent: 3em;\">2、体检介绍后请将体检者指引单交到领表出。</p>\r\n            <div>\r\n                <p style=\"margin-top: 30px; margin-left: 440px;\">受检人签名:</p>\r\n                <p style=\"text-indent: 3em; margin-top: 30px; margin-left: 440px;\">日期:&nbsp;&nbsp;@TJTime</p>\r\n            </div>\r\n          </div>\r\n        </div>\r\n</body>\r\n</html>");  LODOP.ADD_PRINT_BARCODE(201,147,148,38,"128C","identity");',
'select    t1.PhID AS PhID,    t1.SysNumber AS SysNumber,   REPLACE(cast(t2.Picture AS varchar(2000)),'~','') AS Picture,   t2.MName as MName,   t2.MAge as MAge,   t2.MSex as MSex,   t2.Nation as Nation,   dbo.GetBaseName(t1.Degree) as Degree,   dbo.GetBaseName(t1.IsMarry) as IsMarry,   t1.NowJob as NowJob,   t1.AllWorkYear as AllWorkYear,   t1.DangerousEffects as DangerousEffects,   t1.DangerousYear as DangerousYear,   t1.TemplateName as TemplateName,   t1.UnitName as UnitName,   dbo.GetBaseName(t3.MemberType) as MemberType,   dbo.GetBaseName(t3.PhysicalType) as PhysicalType,   CONVERT(varchar(100), GETDATE(), 23) as TJTime  from Physical_Info as t1  left join Physical_MemberInfo as t2 on t1.ArchivesCode = t2.ArchivesCode  left join Physical_Template as t3 on t1.TemplateID = t3.TemplateID',0,1,'职业健康检查指引单数据来源')